    private void parseItem(List<GalleryItem> items,JSONObject jsonbody)throws IOException,JSONException{
        JSONObject jsonObject=jsonbody.getJSONObject("photos");
        JSONArray jsonArray=jsonObject.getJSONArray("photo");

        for(int i=0;i<jsonArray.length();i++){
            JSONObject photo=jsonArray.getJSONObject(i);

            GalleryItem item=new GalleryItem();
            item.setId(photo.getString("id"));
            item.setCaption(photo.getString("title"));
            if(!photo.has("url_s")){
                continue;
            }
            item.setUrl(photo.getString("url_s"));
            items.add(item);
        }

    }


